using SharpSRTP.DTLSSRTP;
using System;
using System.Linq;

namespace SharpSRTP.Tests
{
    /// <summary>
    /// RFC8723 https://www.ietf.org/rfc/rfc8723.html
    /// </summary>
    [TestClass]
    public class TestRFC8723
    {
        // These test vectors were generated by SharpSRTP, they are not official and can be wrong!
        [DataRow(ExtendedSrtpProtectionProfile.DOUBLE_AEAD_AES_128_GCM_AEAD_AES_128_GCM, "f7d54b1f77018d00a48438d4be6d1b59be683885ec77dde0d18a1e1e566044aa00fb3c11179f6a224763350e26634b952e00d0e34f6ef57b41aecbc8216832bbf18389d0f58f861065f337b8ffdb115c585cfef1019b19d71579a1792d0a6ac7467efa2e39ec36a4a75c1ff9c1f599e2", "", "")]
        [TestMethod]
        public void Test_Srtp_Protect_Unprotect(int dtlsSrtpProfile, string masterKeySalt, string rtp, string expectedSrtp)
        {
            byte[] masterKeySaltBytes = Convert.FromBase64String(masterKeySalt);
            byte[] rtpBytes = Convert.FromHexString(rtp);

            byte[] MKI = null;
            var keys = DtlsSrtpProtocol.CreateMasterKeys(dtlsSrtpProfile, MKI, masterKeySaltBytes);

            var context = DtlsSrtpProtocol.CreateSrtpClientSessionContext(keys);
            byte[] srtpBytes = new byte[context.CalculateRequiredSrtpPayloadLength(rtpBytes.Length)];
            Buffer.BlockCopy(rtpBytes, 0, srtpBytes, 0, rtpBytes.Length);
            int ret = context.ProtectRtp(srtpBytes, rtpBytes.Length, out int len);

            string srtpString = Convert.ToHexString(srtpBytes.Take(len).ToArray()).ToLowerInvariant();
            Assert.AreEqual(expectedSrtp, srtpString);

            var decodeContext = DtlsSrtpProtocol.CreateSrtpServerSessionContext(keys);
            ret = decodeContext.UnprotectRtp(srtpBytes, srtpBytes.Length, out int olen);

            string rtpString = Convert.ToHexString(srtpBytes.Take(olen).ToArray()).ToLowerInvariant();
            Assert.AreEqual(rtp, rtpString);
        }
    }
}
